var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var goupSchema = mongoose.Schema({
    token: String,
    groupName: String,
    groupMembers: Array,
    groupMembersName:Array,
    groupRequests: Array,
    groupRequestsName:Array,
    groupAdmin: String,
    groupAdminName:String
});

mongoose.connect('mongodb://localhost:27017/groupay-backend');

module.exports = mongoose.model('groups', goupSchema);