var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var userSchema = mongoose.Schema({
    token: String,
    firstName: String,
    lastName: String,
    email: String,
    groups: Array,
    adminGroups: Array,
    groupRequests: Array,
    hashed_password: String,
    salt: String,
    temp_str: String
});

//mongoose.connect('mongodb://localhost:27017/groupay-backend');
module.exports = mongoose.model('users', userSchema);